rule "remote_addr geoip lookup"
when
  has_field("remote_addr")
then
  let geo = lookup("geoip-lookup", to_string("remote_addr"));
  set_field("remote_addr_geo_location", geo["coordinates"]);
  set_field("remote_add_geo_country_code", geo["country"].iso_code);
  set_field("remote_addr_geo_country_name", geo["country"].names.en);
  set_field("remote_addr_geo_city_name", geo["city"].names.en);
end
